import {gql} from '@apollo/client'

export const LOGIN_HOSPITAL = gql`
    mutation LoginHospital(
        $email:String!,
        $password:String!
    ){
        loginHospital(input:{
            email:$email,
            password:$password
        }){
            message
            status
            token
        }
    }
`


export const GET_ACCESS_BY_HOSPITAL = gql`
    query{
        getAccessByHospital{
            id
            createdAt
            user{
                id
                name
            }
        }
    }

`


export const GET_ALL_RECORD_BY_USER_ID = gql`
    query GetAllRecordByUserId($userId:Int){
        getAllRecordByUserId(userId:$userId){
            transId
            createdAt
            hospital{
                name
            }
          }
    }
`

export const CREATE_RECORD = gql`
    mutation CreateRecord(
        $bp:String,
        $diabetics:String,
        $allergies:String,
        $smoke:String,
        $drink:String,
        $surgery:String,
        $admission:String,
        $medication:String,
        $kids:String,
        $userId:Int
    ){
        createRecord(input:{
            bp:$bp,
            diabetics:$diabetics,
            drugAllergy:$drugAllergy,
            smoke:$smoke,
            drink:$drink,
            sugery:$sugery,
            admission:$admission,
            medication:$medication,
            kids:$kids,
            userId:$userId
        }){
            message
            status
        }
    }
`
