import React,{useState} from 'react'
import Nav from '../../../components/layout'
import Link from 'next/link'
import {Modal} from '../../components/modal'
import {useQuery,useMutation} from '@apollo/client'
import {GET_ALL_RECORD_BY_USER,CREATE_RECORD} from '../../../graphql/schema'
import {useRouter} from 'next/router'
import {Formik,Form,Field} from 'formik'
import {toast} from 'react-toastify'



const Index = ()=>{
    const [isOpen,setIsOpen] = useState(false)
    const {query} = useRouter()

    const id = query.id.split("_")

    
   const {data,loading,refetch} =  useQuery(GET_ALL_RECORD_BY_USER)



return(
        <>
            <Nav />
            <br /><br />
            
            <div className="container">
            <h5>Medical Records <button className="btn right indigo">Give Access</button></h5><br />
               {
                   loading ? <p>Loading...</p>:
                   <table className="striped">
                   <thead>
                   <tr><th>#</th><th>Hospital</th><th>Created</th><th>...</th></tr>
                   </thead>
                   <tbody>
                       {
                           data ? data.getAllRecordByUser.map(({transId,hospital,createdAt},i)=>(

                               <tr><td>{1+i}.</td><td>{hospital.name}</td><td>{createdAt}</td><td><Link href="/hos/info/[tid]" as={`/hos/info/${transId}`}><a><i className="material-icons">visibility</i></a></Link></td></tr>
                           )):null
                       }
                       </tbody>
               </table>
               }
               
            </div>

             <style jsx>{`
                .input-field{
                    margin-left:40px !important;
                }    
            `}</style>
        </>
    )
}


export default Index
