import React,{useState} from 'react'
import Nav from '../components/layout'
import Link from 'next/link'
import {useQuery,useMutation} from '@apollo/client'
import {GET_ALL_RECORD_BY_USER,CREATE_ACCESS} from '../graphql/schema'
import {Formik,Form,Field} from 'formik'
import {toast} from 'react-toastify'
import {Modal} from '../components/modal'
import {getClient} from '../graphql/client'



const Index = ()=>{
    const [isOpen,setIsOpen] = useState(false)
  
   const {data,loading,refetch} =  useQuery(GET_ALL_RECORD_BY_USER)

   const [createAccess] = useMutation(CREATE_ACCESS,{
       onCompleted({createAccess}){
           if(createAccess.status){
                refetch()
               toast.success(createAccess.message)
           }else{
            toast.error(createAccess.message)
           }
       },
       onError(error){

       }
   })


return(
        <>
            <Nav />
            <br /><br />
            <div className="container">
            <h5>Profile <button className="btn right indigo" onClick={()=>setIsOpen(true)}>Give Access</button></h5><br />
            <ul>
                    <li><b>Name:</b><br />Abiodun Michael</li><br />
                    <li><b>Email:</b><br />Abiodun Michael</li><br />
                    <li><b>Phone Number:</b><br />Abiodun Michael</li><br />
                    <li><b>Date of Birth:</b><br />Abiodun Michael</li><br />
                    <li><b>Gender:</b><br />Abiodun Michael</li><br />
                    <li><b>Address:</b><br />Abiodun Michael</li><br />
                    <li><b>Blood Group:</b><br />Abiodun Michael</li><br />
                    <li><b>Genotype:</b><br />Abiodun Michael</li><br />
                    
                </ul>
               
               <Modal title="Give Access" isOpen={isOpen} 
                close={()=>setIsOpen(false)} 
                isLine={true} overflow={true}>
                     <div style={{width:"100%"}}>
                        <div className="row">
                            <div className="col s12 m6 input-field">
                            <Formik
                            initialValues={{email:""}}
                            onSubmit={(input,{setSubmitting,resetForm})=>{
                                createAccess({variables:input}).then(r=>{
                                    if(r.data.createAccess.status){
                                        resetForm()
                                        setIsOpen(false)
                                    }
                                    setSubmitting(false)
                                })

                            }}>{({isSubmitting})=>(
                            <Form>
                                <Field name="email" placeholder="Enter Hospital Email"/>
                                <button className="btn indigo" disabled={isSubmitting}>{isSubmitting ? "Please wait...":"Submit"}</button>
                            </Form>
                            )}
                        </Formik>
                            </div>
                        </div>
                    </div>
                </Modal>
            </div>

             <style jsx>{`
                .input-field{
                    margin-left:40px !important;
                }    
            `}</style>
        </>
    )
}

export async function getServerSideProps({req,res}){
    getClient(req)
     return{props:{}}
 }

export default Index
