import React,{useState} from 'react'
import Nav from '../../../components/layout'
import Link from 'next/link'
import {Modal} from '../../../components/modal'
import {useQuery,useMutation} from '@apollo/client'
import {GET_ALL_RECORD_BY_USER_ID} from '../../../graphql/schema'
import {useRouter} from 'next/router'



const Index = ()=>{
    const [isOpen,setIsOpen] = useState(false)
    const {query} = useRouter()
    
   const {data,loading} =  useQuery(GET_ALL_RECORD_BY_USER_ID,{variables:{userId:1}})


return(
        <>
            <Nav />
            <br /><br />
            
            <div className="container">
            <h5>Abiodun's Medical Records <button className="btn right indigo" onClick={()=>setIsOpen(true)}>Add Record</button></h5><br />
               {
                   loading ? <p>Loading...</p>:
                   <table className="striped">
                   <thead>
                   <tr><th>#</th><th>Hospital</th><th>Created</th><th>...</th></tr>
                   </thead>
                   <tbody>
                       {
                           data ? data.getAllRecordByUserId.map(({transId,hospital,createdAt},i)=>(

                               <tr><td>{1+i}.</td><td>{hospital.name}</td><td>{createdAt}</td><td><Link href="/hos/info/[tid]" as={`/hos/info/${transId}`}><a><i className="material-icons">visibility</i></a></Link></td></tr>
                           )):null
                       }
                       </tbody>
               </table>
               }
               
            </div>

            <Modal title="Create Record" isOpen={isOpen} 
                close={()=>setIsOpen(false)} 
                isLine={true}>
                    <div style={{width:"100%"}}>
                        <div className="row">
                            <div className="col s12 m6 input-field">
                                <input type="text" id="bp" placeholder="Blood pressure"/>
                                <label htmlFor="bp">First Name</label>
                            </div>
                        </div>
                    </div>
                    
            </Modal>
            <style jsx>{`
                .input-field{
                    margin-left:40px !important;
                }    
            `}</style>
        </>
    )
}


export default Index
