import {gql} from '@apollo/client'

export const LOGIN_HOSPITAL = gql`
    mutation LoginHospital(
        $email:String!,
        $password:String!
    ){
        loginHospital(input:{
            email:$email,
            password:$password
        }){
            message
            status
            token
        }
    }

`


export const GET_ACCESS_BY_HOSPITAL = gql`
    query{
        getAccessByHospital{
            id
            createdAt
            user{
                id
                name
            }
        }
    }

`


export const GET_ALL_RECORD_BY_USER_ID = gql`
    query GetAllRecordByUserId(){
        getAllRecordByUserId($userId:Int){
            transId
            patientId
            createdAt
          }
    }

`
