import React,{useState} from 'react'
import Nav from '../../../components/layout'
import Link from 'next/link'
import {Modal} from '../../../components/modal'
import {useQuery,useMutation} from '@apollo/client'
import {GET_ALL_RECORD_BY_USER_ID,CREATE_RECORD} from '../../../graphql/schema'
import {useRouter} from 'next/router'
import {Formik,Form,Field} from 'formik'
import {toast} from 'react-toastify'



const Index = ()=>{
    const [isOpen,setIsOpen] = useState(false)
    const {query} = useRouter()

    const id = query.id.split("_")

    
   const {data,loading,refetch} =  useQuery(GET_ALL_RECORD_BY_USER_ID,{variables:{userId:parseInt(id[0])}})

   const [createRecord] = useMutation(CREATE_RECORD,{
       onCompleted({createRecord}){
        if(createRecord.status){
            refetch()
            toast.success(createRecord.message)
        }else{
            toast.error(createRecord.message)
        }
       },
       onError(errors){

       }
   })


return(
        <>
            <Nav />
            <br /><br />
            
            <div className="container">
            <h5>{id[1]}'s Medical Records <button className="btn right indigo" onClick={()=>setIsOpen(true)}>Add Record</button></h5><br />
               {
                   loading ? <p>Loading...</p>:
                   <table className="striped">
                   <thead>
                   <tr><th>#</th><th>Hospital</th><th>Created</th><th>...</th></tr>
                   </thead>
                   <tbody>
                       {
                           data ? data.getAllRecordByUserId.map(({transId,hospital,createdAt},i)=>(

                               <tr><td>{1+i}.</td><td>{hospital.name}</td><td>{createdAt}</td><td><Link href="/hos/info/[tid]" as={`/hos/info/${transId}`}><a><i className="material-icons">visibility</i></a></Link></td></tr>
                           )):null
                       }
                       </tbody>
               </table>
               }
               
            </div>

            <Modal title="Create Record" isOpen={isOpen} 
                close={()=>setIsOpen(false)} 
                isLine={true} overflow={true}>
                    <Formik
                        enableReinitialize={true}
                        initialValues={{bp:'',diabetics:'',drugAllergy:'',smoke:'',drink:'',surgery:'',allergies:'',admission:'',medication:'',kids:'',mode:'', userId:parseInt(id[0])}}
                        onSubmit={(input,{setSubmitting,resetForm})=>{
                            createRecord({variables:input}).then((res)=>{
                                if(res.data.createRecord.status){
                                    resetForm()
                                    setIsOpen(false)
                                }
                                setSubmitting(false)
                            })
                        }}>
                            {({handleSubmit,isSubmitting})=>(
                        <Form>
                            <div style={{width:"100%"}}>
                        <div className="row">
                            <div className="col s12 m6 input-field">
                                <Field type="text" name="bp" placeholder="Last Bp"/>
                                <Field type="text" name="diabetics" placeholder="Diabetes History"/>
                                <Field type="text" name="allergies" placeholder="Drug Allergies"/>
                                <Field type="text" name="smoke" placeholder="Smoke (Yes/No)"/>
                                <Field type="text" name="drink" placeholder="Drink (Yes/No)"/>
                                <Field type="text" name="surgery" placeholder="Surgery History"/>
                                <Field type="text" name="admission" placeholder="Admission History"/>
                                <Field type="text" name="medication" placeholder="Present Medication"/>
                                <p>For Female Patients</p>
                                <Field type="text" name="kids" placeholder="Number of kids"/>
                                <Field type="text" name="mode" placeholder="Method of Delivery (CS/Normal)"/>
                                <br /><br />
                                <button className="btn indigo btn-medium" disabled={isSubmitting}  onClick={handleSubmit}>{isSubmitting ? "Please wait...":"Submit"}</button>
                            </div>
                        </div>
                    </div>
                        </Form>
                    )}</Formik> 
            </Modal>
            <style jsx>{`
                .input-field{
                    margin-left:40px !important;
                }    
            `}</style>
        </>
    )
}


export default Index
