import React from 'react'
import Nav from '../../components/layout'
import Link from 'next/link'
import {useQuery} from '@apollo/client'
import {GET_ACCESS_BY_HOSPITAL} from '../../graphql/schema'


const Dashboard = ()=>{

    const {data,loading} = useQuery(GET_ACCESS_BY_HOSPITAL)


    return(
        <>
            <Nav />
            <br /><br />
            
            <div className="container">
            <h5>Patient list</h5>
            {
                loading ? <p>Loading...</p>:
                <table className="striped">
                    <thead>
                    <tr><th>#</th><th>Customer Name</th><th>Created</th><th>...</th></tr>
                    </thead>
                    <tbody>
                        {
                            data ? data.getAccessByHospital.map(({id,createdAt,user},i)=>(
                                <tr key={i}><td>{1+i}.</td><td><Link href="#"><a>{user.name}</a></Link></td><td>{new Date(createdAt).getDate()}</td><td><Link as={`/hos/record/${user.id}_${user.name}`} href="/hos/record/[id]"><a><i className="material-icons">visibility</i></a></Link></td></tr>
                            )):null
                        }
                      
                        </tbody>
                </table>
            }
                
            </div>
        </>
    )
}

export default Dashboard
