import {gql} from '@apollo/client'

export const LOGIN_HOSPITAL = gql`
    mutation LoginHospital(
        $email:String!,
        $password:String!
    ){
        loginHospital(input:{
            email:$email,
            password:$password
        }){
            message
            status
            token
        }
    }

`


export const GET_ACCESS_BY_HOSPITAL = gql`
    query{
        getAccessByHospital{
            name
            patientId
            id
            createdAt
        }
    }

`
