import React,{useState} from 'react'
import Nav from '../../components/layout'
import Link from 'next/link'
import {useQuery, useLazyQuery} from '@apollo/client'
import {GET_ACCESS_BY_HOSPITAL,GET_USER_INFO_BY_ID} from '../../graphql/schema'
import {Modal} from '../../components/modal'


const Dashboard = ()=>{
    const [isOpen, setIsOpen] = useState(false)
    const {data,loading} = useQuery(GET_ACCESS_BY_HOSPITAL)

    const [getUserInfoById, {data:userInfo, loading:loadingInfo}] = useLazyQuery(GET_USER_INFO_BY_ID)


        const handleUser = (id)=>{
            getUserInfoById({variables:{id:id}})
            setIsOpen(true)
        }


    return(
        <>
            <Nav />
            <br /><br />
            <button className="btn" onClick={()=>setIsOpen(true)}>Click</button>
            <div className="container">
            <h5>Patient list</h5>
            {
                loading ? <p>Loading...</p>:
                <table className="striped">
                    <thead>
                    <tr><th>#</th><th>Customer Name</th><th>Created</th><th>...</th></tr>
                    </thead>
                    <tbody>
                        {
                            data ? data.getAccessByHospital.map(({id,createdAt,user},i)=>(
                                <tr key={i}><td>{1+i}.</td><td className="user" onClick={()=>handleUser(id)}>{user.name}</td><td>{new Date(createdAt).getDate()}</td><td><Link as={`/hos/record/${user.id}_${user.name.replace(/\s+/g, '-').toLowerCase()}`} href="/hos/record/[id]"><a><i className="material-icons">visibility</i></a></Link></td></tr>
                            )):null
                        }
                      
                        </tbody>
                </table>
            }
                 <Modal title="User Profile" isOpen={isOpen} 
                close={()=>setIsOpen(false)} 
                isLine={true} overflow={true}
                width={30}>
                    <div style={{width:"100%", paddingLeft:"30px"}}>
                    <div className="row">
                        <div className="col s12">

                    <p><b>Name:</b> abiodun Michael</p>
                    <p><b>Email:</b> Abiodun Michael</p>
                    <p><b>Phone:</b> abiodun Michael</p>
                    <p><b>Address:</b> abiodun Michael</p>
                    <p><b>Date of Birth:</b> abiodun Michael</p>
                    <p><b>Gender:</b> abiodun Michael</p>
                    <p><b>Marital Status:</b> abiodun Michael</p>
                    <p><b>Genotype:</b> abiodun Michael</p>
                    <p><b>Blood Group:</b> abiodun Michael</p>
                        </div>
                    </div>
                    </div>
                </Modal>
            </div>

            <style jsx>{`
                .user{
                    text-decoration:underline;
                }
                
            `}</style>
        </>
    )
}

export default Dashboard
