import React from 'react'
import {Formik,Field,Form} from 'formik'


const Index = ()=>{

    return(
        <>
        <div className="wrapper">
            <div className="white-bg">
                <div className="form-wrapper">
                    <h3>Partner Login</h3>
                    <Formik
                        validationSchema={LoginSchema}
                        initialValues={{email:"",password:""}}
                        onSubmit={(input,{setSubmitting})=>{
                            handleSubmit(input).then(r=>{
                                if(r.status){
                                 
                                }
                                setSubmitting(false)
                            })
                        }}>{({handleSubmit, isSubmitting})=>(
                        <Form>
                            <Field type="email" placeholder="Email address" className="input" name="email"/>
                            <Field type="password" placeholder="Password" className="input"  name="password"/>
                        <button className="login-btn" onClick={handleSubmit} disabled={isSubmitting}>{isSubmitting ? "Please wait":"Login"}</button>
                        </Form>
                    )}</Formik>
                    <p className="link">Don't have an account ? <Link href="/register"><a>Create account</a></Link></p>
                </div>
            </div>

        </div>
        <style jsx>{`
            .wrapper{
                height:100%;
                display:flex; 
                flex-direction:column;
                align-items:center;
                justify-content:center;
            }

            .white-bg{
                height:400px;
                width:400px;
                background:#ffffff;
                border-radius:4px;
                display:flex;
                align-items:center;
                justify-content:center;
            }

            .form-wrapper{
                height:65%;
                width:70%;

            }

            .form-wrapper input{
                display:block;
                outline:none;
                border:none;
                background-color:#F7F7F7;
                height:50px;
                padding: 0 10px;
                width:100%;
                margin-top:20px;
                color:#222222;
            }

            .login-btn{
                width:100%;
                outline:none;
                border:none;
                height:40px;
                margin-top:20px;
                background:#0277bd;
                color:white;
                cursor:pointer;
            }

            h3{
                color:#a5d6a7
            }
        `}</style>
        </>
    )
}

export default Index
